import React from 'react'

//Material-UI
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { Grid, Paper } from '@material-ui/core';
import InputAdornment from '@material-ui/core/InputAdornment';

// Icon's
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import LockOpenTwoToneIcon from '@material-ui/icons/LockOpenTwoTone';

//Firebase
import fire from '../config.js/fireBaseConfiguration'

class Login extends React.Component {
    constructor() {
        super()
        this.state = {
            email: '',
            password : '',
            error: ''
        }
    }

    handleChange = (e) => {
        e.preventDefault()
        this.setState({ [e.target.name]: e.target.value })
    }

    handleSubmit = (userData) => {
        fire.auth().signInWithEmailAndPassword(userData.email, userData.password)
        .then(response => {
            console.log(response.user);
        })

        .catch(function(error) {
            // Handle Errors here.
            var errorCode = error.code;
            var errorMessage = error.message;

            this.setState ({
                error : error.message
            })
          });

    }

    render() {
        return (
            <div>

                <Grid
                    container
                    spacing = {50}
                >
                    <Grid 
                        item xs={3} 
                    >

                    </Grid>


                        <Grid 
                            item xs={6} 
                        >
                            <Paper style={{ padding: 20 }} >

                                <TextField
                                    label="E-mail"
                                    style={{ margin: 8 }}
                                    placeholder="xyz@gmail.com"
                                    name='email'
                                    onChange={this.handleChange}
                                    fullWidth
                                    margin="dense"
                                    autoComplete='off'
                                    InputProps={{
                                        shrink: true,
                                        startAdornment: (
                                            <InputAdornment position="start">
                                                <AccountCircleIcon color='primary' />
                                            </InputAdornment>
                                        )
                                    }}
                                /> <br />

                                <TextField
                                    label="Password"
                                    style={{ margin: 8 }}
                                    placeholder="Password"
                                    name='password'
                                    type='password'
                                    onChange={this.handleChange}
                                    fullWidth
                                    margin="dense"
                                    autoComplete='off'
                                    InputProps={{
                                        shrink: true,
                                        startAdornment: (
                                            <InputAdornment position="start">
                                                <LockOpenTwoToneIcon color='primary' />
                                            </InputAdornment>
                                        )
                                    }}
                                /> <br />

                                <Button
                                    variant="contained"
                                    color = 'primary'
                                    onClick = { () => this.handleSubmit( {email: this.state.email, password: this.state.password } ) }
                                >
                                    Sign in
                                </Button>
                            </Paper>

                        </Grid>

                    <Grid item xs={3} >

                    </Grid>

                </Grid>

            </div>
        )
    }
}

export default Login;
